class BaiduAIOCRClient:
    def __init__(self, ak, sk):
        self.ak = ak
        self.sk = sk
        self.session = requests.Session()
        url = "https://aip.baidubce.com/oauth/2.0/token"
        resp = self.session.get(url, params={
            "grant_type": "client_credentials",
            "client_id": self.ak,
            "client_secret": self.sk
        },
        proxies={"https": "localhost:12580"}, verify=False)
        data = resp.json()
        self.accesstoken = data["access_token"]

    def ocr(self, sub_images, **kwargs):
        url = "https://aip.baidubce.com/rest/2.0/ocr/v1/general"
        out = []
        generator = handle_sub_images(sub_images, out)
        next_image = next(generator)
        while True:
            with io.BytesIO() as f:
                next_image.save(f, format="PNG")
                base64_encoded_im = base64.b64encode(f.getvalue())
            resp = self.session.post(
                url,
                params={"access_token": self.accesstoken},
                data={"image": base64_encoded_im},
                proxies={"https": "localhost:12580"},
                verify=False
            )
            data = resp.json()
            value = [
                (res["location"], res["words"])
                for res in data["words_result"]
            ]
            try:
                next_image = generator.send(value)
            except StopIteration:
                break
        return out
